import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';

var flag = false;
function App() {
  var [location, setLocation] = useState("");
  var [Weather, setWeather] = useState("");



  const locate = (e) => {
    setLocation(e.target.value);
    console.log(Weather);
    axios.get(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=${location}`)
      .then(response => {
        setWeather(response.data);
        console.log(Weather);
        flag=true;
      })
  }

  useEffect(() => {
    axios.get(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=auto:ip`)
    .then(response => {
      setWeather(response.data);

    })
  },[location]);



  return (
    <div id='container'>
      <div id='detailsContainer'>
        <h1> Welcome to Weather Finder</h1>
       <div style={{display:"flex",alignItems:"center",justifyContent:"space-between"}}> <input type="text" onChange={locate} placeholder="Search here" autoFocus /> <i className="fa-solid fa-magnifying-glass"></i></div>
      </div>
      {flag && <div id='resultContainer'>
          {Weather.map((val)=>{
            <h1>{val.region}</h1>
          })}
       </div>}
    </div>
  );
}

export default App;
