import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';


function App() {
  var [location, setLocation] = useState("");
  var [Weather,setWeather] = useState([]);


  useEffect(() => {
    axios.get(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=${location}`)
      .then(response => {
        setWeather(response.data);
        console.log(Weather);
      })
  }, [location])

  return (
    <div id='container'>
      <Search />
      {Weather && <Details />}
    </div>
  );
}

export default App;

export const Search = () => {
  const locate = (e) => {
      setLocation(e.target.value);
    }
return (
 <>
  <h1> Welcome to Weather Finder</h1>
    <input  type="text" onChange={locate} autoFocus />
 </>
)
}
