import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';

var flag = true;
function App() {
  var [location, setLocation] = useState('Lucknow');
  var [Weather, setWeather] = useState([]);
  var [count, setCount] = useState(0);




  const locate = (e) => {
    setLocation(e.target.value);
  }



  return (
    <div id='container'>
      <div id='detailsContainer'>
        <h1> Welcome to Weather Finder</h1>
        <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}> <input type="text" onChange={locate} placeholder="Search here" autoFocus id='input' /> <i className="fa-solid fa-magnifying-glass" ></i></div>
      </div>
      <div id='weatherContainer'>
        {useEffect(() => {
          fetch(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=$location=${location}`)
            .then(resp => resp.json())
            .then(data => {
              setWeather(data);
              setCount(count + 1);
            })
        }, [location])}

      </div>
    </div>
  );
}

export default App;
