import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';

function App() {
  var [location, setLocation] = useState("");
  var [Weather, setWeather] = useState([]);


  const locate = (e) => {
    setLocation(e.target.value);
  }

  useEffect(() => {
    axios.get(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=${location}`)
      .then(response => {
        setWeather(response.data);
        console.log(Weather);
      })
  }, [location])

  return (
    <div id='container'>
      <div id='detailsContainer'>
        <h1> Welcome to Weather Finder</h1>
        <input type="text" onChange={locate} placeholder="Search here" autoFocus />
      </div>
      <div id='resultContainer'>
        {Weather.map((val)=>{
          return(
            <div> {va} </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
