import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';
import Search from './Components/Search';

function App() {
  var [location, setLocation] = useState("");
  const locate = (e) => {
    setLocation(e.target.value);
  }

  useEffect(() => {
    axios.get(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=${location}`)
      .then(response => {
        const Weather = response.data;
        console.log(Weather);
      })
  }, [location])

  return (
    <div id='container'>
      <Search />
      {}
    </div>
  );
}

export default App;
