import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';

function App() {
  var [location, setLocation] = useState("");
  var [Weather, setWeather] = useState();


  const locate = (e) => {
    setLocation(e.target.value);
  }

  useEffect(() => {
    axios.get(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=${location}`)
      .then(response => {
        setWeather(response.data);
        console.log(Weather);
      })
  }, [location]);



  return (
    <div id='container'>
      <div id='detailsContainer'>
        <h1> Welcome to Weather Finder</h1>
       <div style={{display:"flex"}}> <input type="text" onChange={locate} placeholder="Search here" autoFocus /> <i class="fa-solid fa-magnifying-glass"></i></div>
      </div>
      <div id='resultContainer'>

      </div>
    </div>
  );
}

export default App;
