import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';
import WeatherComponent from './Components/WeatherComponent';

var flag = true;
var data = [];
function App() {
  var [location, setLocation] = useState('');
  var [Weather, setWeather] = useState([]);
  var [count, setCount] = useState(0);

  const locate = (e) => {
    if (e.key === 'Enter') {
      fetch(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=$location=${location}`)
        .then(resp => resp.json())
        .then(data => {
          setWeather(data);
          setCount(count + 1);
          console.log(Weather);
        });
    }
  }
  useEffect(() => {
    fetch(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=$location=`)
      .then(resp => resp.json())
      .then(data => {
        setWeather(data);
        setCount(count + 1);
        console.log(Weather);
      });
  })


  return (
    <div id='container'>
      <div id='detailsContainer'>
        <h1> Welcome to Weather Finder</h1>
        <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}> <input type="text" onKeyPress={locate} placeholder="Search here" autoFocus id='input' value={location} onChange={e => setLocation(e.target.value)} /> <i className="fa-solid fa-magnifying-glass" ></i></div>
      </div>
      {Weather === [] ? <WeatherComponent Weather={Weather} /> : <div id="weatherContainer">
        <div> <h1> {Weather.location.name}, {Weather.location.region} </h1></div>
        <div>
          <div>
            <i className="fa-solid fa-wind"></i>
            <span></span>
          </div>
          <div>
            <i className="fa-solid fa-cloud"></i>
            <span>dfsgsdfg</span>
          </div>
        </div>

        <div>
          <div>
            <i className="fa-solid fa-temperature-high"></i>
            <span>dfsgdfsg</span>
          </div>
          <div>
            <i class="fa-solid fa-sun"></i>
            <i class="fa-solid fa-moon"></i>
            <span>dfsgdfsg</span>

          </div>
        </div>
      </div>}

    </div>
  );
}

export default App;
