import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';
import WeatherComponent from './Components/WeatherComponent';

var flag = true;
var data = [];
function App() {
  var [location, setLocation] = useState('Lucknow');
  var [Weather, setWeather] = useState([]);


  useEffect(() => {
    const fetchApi = async () => {
      await fetch(`https://api.weatherapi.com/v1/current.json?key=0bab7dd1bacc418689b143833220304&q=$location=${location}`)
        .then(resp => resp.json())
        .then(dat => {
          setWeather(data);
        })
    }
    fetchApi();
  }, [location]);

  return (
    <div id='container'>
      <div id='detailsContainer'>
        <h1> Welcome to Weather Finder</h1>
        <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}> <input type="text" onChange={(e)=>{setLocation(e.target.value)}} placeholder="Search here" autoFocus id='input' /> <i className="fa-solid fa-magnifying-glass" ></i></div>
      </div>
      <div id='weatherContainer'>
       { Weather === [] ?  <><div id="weatherContainer">
                <div>
                    <i class="fa-solid fa-wind"></i>
                    {console.log("hr")}
                </div>
            </div></> :  <WeatherComponent Weathe={Weather} />}

      </div>
    </div>
  );
}

export default App;
